import styled from "styled-components";
import { FONT_SIZE, COLORS } from "../../utils/constants";

export const BentoGrid = styled.div`
  display: grid;
  grid-template-columns: 6fr 5fr;
  grid-auto-rows: auto;
  height: calc(100% - 2rem);
  width: 100%;
  background: linear-gradient(to bottom, #FBBAD8 0%, #FDE2E9 100%);
`;

export const NavBar = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  background-color: #332c2e;
  width: auto;
  padding: 10px;
  border-radius: 40px 0px 0 40px;
`;

export const MainContainer = styled.div`
  display: flex;
  gap: 10px;
  background-color: ${COLORS.BACKGROUND.GREY_2};
  margin: 1rem;
  border-radius: 40px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

export const ProfileContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  position: relative;
`

export const StyledButton = styled.button`
  background-color: ${COLORS.BACKGROUND.REGULAR_PINK};
  color: black;
  border-radius: 20px;
  padding: 10px 20px;
  margin: 1rem;
  display: flex;
  align-items: center;
  gap: 10px;
  max-width: fit-content;
  font-size: ${FONT_SIZE.EXTRA_LARGE};
`

export const SideContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  background-color: #f5f5f5;
  margin: 15px 0;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

export const SmallContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  padding: 10px;
  background-color: #f5f5f5;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  z-index: 1;
`;
